import { Add, Remove } from "@material-ui/icons"
import { useEffect, useState } from "react"
import { useLocation } from "react-router-dom"
import styled from "styled-components"
import Announcement from "../components/Announcement"
import Footer from "../components/Footer"
import Navbar from "../components/Navbar"
import Newsletter from "../components/Newsletter"
import { publicRequest } from "../requestMethods"
import { mobile } from "../responsive"


const Container = styled.div``
const Wrapper = styled.div`
 padding: 50px;
 display: flex;
 ${mobile({ padding: "10px",flexDirection:"column"})}`
const ImgContainer = styled.div`
 flex:1;`
const Image = styled.img`
 width:100%;
 height: 90vh;
 object-fit:cover;
 ${mobile({ height: "40vh"})}`
const InfoContainer = styled.div`
 flex:1;
 padding: 0px 50px;

`
const Title = styled.h1`
 font-weight: 200;`
const Desc = styled.p`
 margin: 20px 0px;`
const Price = styled.span`
 font-weight: 100;
 font-size: 40px;`

const FilterContainer = styled.div`
 width:70%;
 margin: 30px 0px;
 display: flex;
 justify-content: space-between;
 ${mobile({ width: "70%", flexDirection:"column"})};
`
const Filter = styled.div`
 display: flex;
 align-items: center;
 ${mobile({ margin: "10px 0px"})};`
const FilterTitle = styled.span`
 font-size:20px;
 font-weight: 200;`
const FilterColor = styled.div`
 width: 20px;
 height: 20px;
 border-radius:50%;
 background-color: ${props=>props.color};
 margin: 0px 5px;
 cursor: pointer;`
const FilterSize = styled.select`
 margin-left: 10px;
 padding: 5px;`

const FilterSizeOption = styled.option``
const AddContainer = styled.div`
 width:60%;
 display: flex;
 align-items: center;
 justify-content: space-between;
 ${mobile({ width: "100%"})};`
const AmountContainer = styled.div`
 display: flex;
 align-items: center;
 font-weight: 700;`
const Amount = styled.span`
 width:30px;
 height:30px;
 border-radius:10px;
 border: 1px solid teal;
 display: flex;
 align-items: center;
 justify-content: center;
 margin: 0px 5px;`
const Button = styled.button`
 padding: 15px;
 border: 2px solid teal;
 background-color: white;
 cursor: pointer;
 font-weight: 500;
 
 &:hover{
     background-color: #f7f7f7;
 }`
const Product = () => {
    const location = useLocation();
    const id= location.pathname.split("/")[2]
    const [product, setProduct] = useState({});

    useEffect(()=>{
        const getProduct = async () =>{
            try{
                const res = publicRequest.get("/products/find/"+id)
                setProduct(res.data);
            }catch{}
        }
        getProduct();
    },[id])
    return (
        <Container>
            <Navbar />
            <Announcement />
            <Wrapper>
                <ImgContainer>
                    <Image src={product.img} />
                </ImgContainer>
                <InfoContainer>
                    <Title>Denim Jumpsuit</Title>
                    <Desc>Lorem ipsum dolor sit, amet consectetur adipisicing elit. Quae inventore amet mollitia quia ab quidem, molestiae laborum sequi nesciunt pariatur, iste, nemo rem. Aliquam libero cum illo esse illum iure.</Desc>
                    <Price>$ 20</Price>
                    <FilterContainer>
                        <Filter>
                            <FilterTitle>Color</FilterTitle>
                            <FilterColor color = "black"/>
                            <FilterColor color = "darkblue"/>
                            <FilterColor color = "gray"/>
                        </Filter>
                        <Filter>
                            <FilterTitle>Size</FilterTitle>
                            <FilterSize>
                                <FilterSizeOption>XS</FilterSizeOption>
                                <FilterSizeOption>S</FilterSizeOption>
                                <FilterSizeOption>M</FilterSizeOption>
                                <FilterSizeOption>L</FilterSizeOption>
                                <FilterSizeOption>XL</FilterSizeOption>
                            </FilterSize>
                        </Filter>
                    </FilterContainer>
                    <AddContainer>
                        <AmountContainer>
                            <Remove />
                            <Amount>1</Amount>
                            <Add />
                        </AmountContainer>
                        <Button>ADD TO CART</Button>
                    </AddContainer>
                </InfoContainer>
            </Wrapper>
            <Newsletter />
            <Footer />
        </Container>
    )
}

export default Product
