import axios from "axios";

const BASE_URL = "http://localhost:5000/api/"
const TOKEN ="eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjYxYjNkNGYxNWMwYWY4ZTU1NmRmM2MzYSIsImlzQWRtaW4iOnRydWUsImlhdCI6MTYzOTUyOTQwNSwiZXhwIjoxNjM5Nzg4NjA1fQ.jtPrm_OrmMFx8BG9fpXqWMr9SMye1KF6k9RJZiaKvSc";

export const publicRequest = axios.create({
    baseURL: BASE_URL,
});

export const userRequest = axios.create({
    baseURL: BASE_URL,
    header:{token: `Bearer ${TOKEN}`}
})
